import React, { Component } from 'react';
import './App.css';
import axios from 'axios'

const locationUrl = (city) => `https://api.opencagedata.com/geocode/v1/json?key=196b6ac7d96245988d826a6fd49c913d&q=${city}&pretty=1`

const weatherUrl = (lat, lng) => `https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/82317ee0c43e86070fe114fabe7b32b7/${lat},${lng}?units=ca`


class App extends Component {
  constructor() {
    super()

    this.state = {
      latitude: '',
      longitude: '',
    }

    this.getMyLocation = this.getMyLocation.bind(this)
  }

  componentDidMount() {
    this.getMyLocation()
  }

  getMyLocation() {
    const location = window.navigator && window.navigator.geolocation

    if (location) {
      location.getCurrentPosition((position) => {
        this.setState({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        })
      }, (error) => {
        this.setState({ latitude: 'err-latitude', longitude: 'err-longitude' })
      })
    }

  }

  render() {
    const { latitude, longitude } = this.state

    return (
      <div>
        <input type="text" value={latitude} />
        <input type="text" value={longitude} />
      </div>
    )
  }
}

export default App;
