import React, { Component } from "react";
import "./styles/global.scss";
import Skycons from "react-skycons";
import axios from "axios";

const weatherURL = `https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/`;
const weatherAPI = "d3135eb8e134061565fcb7fe736dd07e/";
console.log(weatherAPI);

const getLocation = new Promise((resolve, reject) => {
  if (navigator.geolocation) {
    console.log("Location aquired");
    navigator.geolocation.getCurrentPosition(pos => {
      const coords = {
        lat: pos.coords.latitude,
        long: pos.coords.longitude
      };
      resolve(coords);
    });
  } else {
    alert("Sorry, you need to enable location services in your browser");
  }
});

class App extends Component {
  state = {
    loading: true,
    myLocation: null,
    weatherData: null,
    visibility: false
  };

  componentDidMount() {
    getLocation
      .then(response => {
        console.log(response);
        this.setState({ myLocation: response });
      })
      .then(() => {
        const { lat, long } = this.state.myLocation;
        axios.get(`${weatherURL}${weatherAPI}${lat},${long}`).then(response => {
          console.log(response.data);
          const {
            summary,
            apparentTemperature,
            icon
          } = response.data.currently;
          const { timezone } = response.data;
          this.setState({
            weatherData: response.data,
            summary: summary,
            timezone: timezone,
            apparentTemperature: apparentTemperature,
            icon: icon.toUpperCase().replace(/-/g, '_'),
            loading: false
          });
        });
      });
  }

  render() {
    const styles = {
      width: "10%",
      height: "10%"

  };
  console.log(this.state.icon)
    return (
      <div className="App">
        <h1>Weather App</h1>
        <h2>Current City: {this.state.timezone}</h2>
        <div>The weather right now is: {this.state.summary}</div>
        <div>{this.state.icon}</div>
        <div>{this.state.apparentTemperature}</div>
        <Skycons color="black" icon={this.state.icon} autoplay={false} style={styles} />

      </div>
    );
  }
}

export default App;


class VisibilityToggle extends React.Component {
  constructor(props) {
      super(props);
      this.handleToggleVisibility = this.handleToggleVisibility.bind(this);
      this.state = {
          visibility: false
      };
  }
  handleToggleVisibility() {
      this.setState((prevState) => {
          return {
              visibility: !prevState.visibility
          };
      });
  }

      render() {
          return (
              <div>
                  <h1>Visibility Toggler!</h1>
                  <button onClick={this.handleToggleVisibility}>
                      {this.state.visibility ? 'Hide details' : 'Show details'}
                  </button>
                  {this.state.visibility && (
                      <div>
                          <p>You can now see and read me man!</p>
                  <p> I am using <strong>state</strong> to toggle this info instead of <em>if</em> and <em>else</em> </p>
                      </div>
                  )}

              </div>
          );
      }


}
