import React, { Component } from 'react';
import './App.css';
import axios from 'axios'


const weatherURL = `https://fcc-weather-api.glitch.me/api/current?lat=${lat}&lon=${lon}`
class App extends Component {
state = {
      lat: null,
      lon: null,
    }


    fetchWeather = (city) => {
      axios.get(locationUrl(city))
      .then(response => {
        const { lat, lng } = response.data.results[0].geometry
        return { lat, lng }
      })
      .then(({ lat, lng}) => {
          axios.get(weatherUrl(lat, lng))
            .then(response => {
              const {summary, temperature} = response.data.currently
              const { timezone } = response.data
              this.setState({
                city: timezone,
                summary,
                temperature,
                data: response.data
              })
            })
      })
    }


  componentWillMount() {

    navigator.geolocation.getCurrentPosition(position => {
      this.setState({ lat: position.coords.latitude, lng: position.coords.longitude });
    }, err => console.log(err)
    );
  }

  render() {

    return (
      <div >
        <h1>Vicky's Awesome Weather App</h1>
        <p>Latitude: {this.state.lat}</p>
        <p>Longitude: {this.state.lng}</p>
      </div>
    );
  }
}

export default App;
