import React, { Component } from 'react';
import './App.css';
import axios from 'axios'


const weatherURL = `https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/`
const weatherAPI = 'f815af3a03b5f0d8c1fbfea0f95d1794/'
const corsAnywhere = 'https://cors-anywhere.herokuapp.com/'

const getLocation = () => {
  if (navigator.geolocation) {
    console.log('Location aquired');
    navigator.geolocation.getCurrentPosition(pos => {
      const coords = {
        lat: pos.coords.latitude,
        long: pos.coords.longitude
      }
      return coords
    })
  } else {
    alert('Sorry, you need to enable location services in your browser')
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      userLocation: null,
      weatherData: null
    };
  }

  componentDidMount() {
    getLocation
      .then(response => {
        this.setState({ userLocation: response });
      })
      .then(() => {
        const { lat, long } = this.state.userLocation;
        axios(
          `${weatherApi.corsAnywhere}${weatherApi.call}${
          weatherApi.key
          }${lat},${long}`
        ).then(res => {
          this.setState({
            weatherData: res.data,
            loading: false
          });
        });
      });
  }

  render() {
    const content = (
  <>
       {this.state.weatherData}

   </>
    );

    return (
      <div className="App">{this.state.loading ? <Spinner /> : content}</div>
    );
  }
}

export default App;
