import React, { Component } from 'react';
import './App.css';
import axios from 'axios'


const weatherURL = `https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/`
const weatherAPI = 'f815af3a03b5f0d8c1fbfea0f95d1794/'


const getLocation = new Promise((resolve, reject) => {
  if (navigator.geolocation) {
    console.log('Location aquired');
    navigator.geolocation.getCurrentPosition(pos => {
      const coords = {
        lat: pos.coords.latitude,
        long: pos.coords.longitude
      }
      resolve(coords)
    })
  } else {
    alert('Sorry, you need to enable location services in your browser')
  }
})

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      userLocation: null,
      weatherData: null
    };
  }

  componentDidMount() {
    getLocation
      .then(response => {
        console.log(response)
        this.setState({ userLocation: response });
      })
      .then(() => {
        const { lat, long } = this.state.userLocation;
        axios.get(
          `https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/${
            weatherAPI
          }${lat},${long}`
        ).then(response => {

          this.setState({
            weatherData: response.data,
            loading: false
          });
        });
      });
  }

  render() {


    return (
      <div className="App">{this.state.weatherData}</div>
    );
  }
}

export default App;
